import React, { useEffect, useState } from "react";
import cartImg from "../../images/typing.png";
import Category from "../child/Category";
import {FaBookReader, FaArrowRight} from "react-icons/fa";
import axios from "axios";

const Home = () => {
    const [books, setBooks] = useState([]);
    
    useEffect(()=>{
        loadBooks();
    });

    const loadBook = async () =>{
        const result = await axios.get("http://localhost:3001/books");
        const showme = setBooks(result.books);
        console.log(showme);
    }
  return (
    <>
      <div class="container">
                <div class="row">
                    <div class="col-md-3">
                        <Category data="Home"/>
                    </div>
                    <div class="col-md-9">
                        <section className="my-g-section">
                        <div className="row">
                        {books.map((book, index) => (
                            <div className="col-md-4">
                                    <div className="card">
                                        <div className="card-image-container">
                                            <img src={cartImg} alt="img"/>
                                        </div>
                                        <div className="card-info-container">
                                                <div className="card-icon-indicate">
                                                        <i><FaBookReader /></i>
                                                </div>
                                                <div className="book-name">
                                                    <h1>{book.title}</h1>
                                                    <p>Author : Prabesh Rijal</p>
                                                    <p>Edition : 5th</p>
                                                    <p>Publisher : Dev</p>
                                                    <p>Publication : 2020/3/9</p>
                                                    <p>Category : Softwares</p>
                                                    <a href="#">Borrow Now<FaArrowRight /></a>
                                                </div>

                                        </div>
                                    </div>
                                    </div> 
                        ))}
                        </div>
                        </section>
                    </div>
                </div>
            </div>
    </>
  );
};

export default Home;
