import React, { useEffect, useState } from "react";
import AdminDashboardForAll from "../AdminDashboardForAll";
import {FaUserEdit, FaTrash, FaPlus} from "react-icons/fa";
import {Link} from "react-router-dom";
import axios from "axios";

const Admin = () =>{
    const [users, setUser] = useState([]);

  useEffect(() => {
    loadUsers();
  }, []);

  const loadUsers = async () => {
    const result = await axios.get("http://localhost:3001/users");
    setUser(result.data.reverse());
  };
  const deleteUser = async id => {
    await axios.delete(`http://localhost:3001/users/${id}`);
    loadUsers();
  };
    return(
        <>
            <section className="admin-global-view-section d-flex">
                <section>
                    <AdminDashboardForAll/>
                </section>
                <section>
                    <div className="container p-3">
                        <div className="row">
                            <div className="col-md-12">
                                <div className="add-something-btn mb-3">
                                    <Link className="btn btn-outline-success" to="/auth/after-auth-admin/admin-dashboard/addadmin">Add Book <i><FaPlus/></i></Link>
                                </div>
                            <table class="table">
                            <thead class="thead">
                                <tr>
                                {/* <th scope="col">No</th>
                                <th scope="col">User Name</th>
                                <th scope="col">Password</th> */}
                                <th scope="col">#</th>
                                <th scope="col">Name</th>
                                <th scope="col">User Name</th>
                                <th scope="col">Email</th>
                                <th>Action</th>
                                </tr>
                            </thead>
                            <tbody>
                                {/* {
                                    users.map((user, index)=>(
                                        <tr>
                                        <th scope="row">{index + 1 }</th>
                                        <td>{user.username}</td>
                                        <td>{user.password}</td>
                                        <td>
                                            <Link to="/auth/after-auth-admin/admin-dashboard/updateadmin" className="btn btn-outline-success mr-2"><i><FaUserEdit/></i></Link>
                                            <Link href="#" className="btn btn-outline-danger"><i><FaTrash/></i></Link>
                                        </td>
                                        </tr>
                                    ))
                                } */}
                                {users.map((user, index) => (
              <tr>
                <th scope="row">{index + 1}</th>
                <td>{user.name}</td>
                <td>{user.username}</td>
                <td>{user.email}</td>
                <td>
                  <Link class="btn btn-primary mr-2" to={`/users/${user.id}`}>
                    View
                  </Link>
                  <Link
                    class="btn btn-outline-primary mr-2"
                    to={`/users/edit/${user.id}`}
                  >
                    Edit
                  </Link>
                  <Link
                    class="btn btn-danger"
                    onClick={() => deleteUser(user.id)}
                  >
                    Delete
                  </Link>
                </td>
              </tr>
            ))}
                            </tbody>
                            </table>
                            </div>
                        </div>
                    </div>
                </section>
            </section>
        </>
    );
}
export default Admin;
