import React, { useState } from "react";
import { useHistory } from "react-router-dom";
import {Link} from "react-router-dom";
import AdminDashboardForAll from "../AdminDashboardForAll";
import {FaStar, FaBackward, FaArrowDown, FaPlus} from "react-icons/fa";
import axios from "axios";

const AddAdmin = () =>{
    let history = useHistory();
    const [admin, setAdmin] = useState({
        uid:"",
        username:"",
        password:""
    });

    const {username, password} = admin;

    const inputEvent = (event) =>{
        setAdmin({ ...admin, [event.target.name]: event.target.value });
    }

    const onSubmitForm = async (event) =>{
        event.preventDefault();
        await axios.post("http://localhost:3001/users", admin)
        .then(history.push("/"))
        .catch(console.log(error))
        ;
    }
    return(
        <>
            <section className="admin-global-view-section d-flex">
                <section>
                    <AdminDashboardForAll/>
                </section>
                <section className="w-100">
                    <div className="container p-3">
                        <div className="row">
                            <div className="col-md-12">
                                <div className="add-something-btn mb-3">
                                    <Link className="btn btn-outline-primary" to="/auth/after-auth-admin/admin-dashboard/admin">Back To List <i><FaBackward/></i></Link>
                                </div>
                            <form onSubmit={onSubmitForm}>
                            <div className="container add-book-form">
                            <div className="function-indicator">
                                <h3>Add New Book <i><FaArrowDown/></i></h3>
                            </div>
                                <div className="row">
                                    <div className="col-md-4">
                                    <div className="form-group">
                                        <label>User Name <i><FaStar/></i></label>
                                        <input  
                                        placeholder="Enter Book Title" 
                                        type="text" className="form-control"  
                                        name="username"
                                        value={username}
                                        onChange={inputEvent}
                                        />
                                    </div>
                                    </div>
                                    <div className="col-md-4">
                                    <div className="form-group">
                                        <label>Password <i><FaStar/></i></label>
                                        <input 
                                        placeholder="Enter Author Name" 
                                        type="text" 
                                        className="form-control"  
                                        name="password"
                                        value={password}
                                        onChange={inputEvent}
                                        />
                                    </div>
                                    </div>
                                </div>
                                    <button type="submit" className="btn btn-success">Add Admin <i><FaPlus/></i></button>
                                </div>
                            </form>
                            </div>
                        </div>
                    </div>
                </section>
            </section>
        </>
    );
}
export default AddAdmin;
