import React, { useEffect, useState } from "react";
import {Link} from "react-router-dom";
import AdminDashboardForAll from "../AdminDashboardForAll";
import {FaUserEdit, FaTrash, FaPlus, FaLongArrowAltDown} from "react-icons/fa";
import axios from "axios";

const AddBook = () =>{
    const [books, setBooks] = useState([]);
    
    useEffect(() => {
        loadBooks();
      }, []);

    const loadBooks = async () => {
        const result = await axios.get("http://localhost:3001/books");
        setBooks(result.data);
      };
    return(
        <>
            <section className="admin-global-view-section d-flex">
                <section>
                    <AdminDashboardForAll/>
                </section>
                <section>
                    <div className="container p-3">
                        <div className="row">
                            <div className="col-md-12">
                                <div className="add-something-btn mb-3">
                                    <Link className="btn btn-outline-primary" to="">Back To List <i><FaBackward/></i></Link>
                                </div>
                            <table class="table">
                            <thead class="thead">
                                <tr>
                                <th scope="col">No</th>
                                <th scope="col">Book Title</th>
                                <th scope="col">Author Name</th>
                                <th scope="col">Edition</th>
                                <th scope="col">Publisher</th>
                                <th scope="col">Publication</th>
                                <th scope="col">Copies</th>
                                <th scope="col">Category</th>
                                <th scope="col">Rack</th>
                                <th scope="col">Actions</th>
                                </tr>
                            </thead>
                            <tbody>
                                {
                                    books.map((book, index)=>(
                                        <tr>
                                        <th scope="row">{index + 1 }</th>
                                        <td>{book.bk_title}</td>
                                        <td>{book.bk_author}</td>
                                        <td>{book.bk_edition}</td>
                                        <td>{book.bk_publisher}</td>
                                        <td>{book.bk_publication_date}</td>
                                        <td>{book.bk_copies}</td>
                                        <td>{book.bk_category}</td>
                                        <td>{book.bk_rack_no}</td>
                                        <td>
                                            <a href="#" className="btn btn-outline-success mr-2"><i><FaUserEdit/></i></a>
                                            <a href="#" className="btn btn-outline-danger"><i><FaTrash/></i></a>
                                        </td>
                                        </tr>
                                    ))
                                }
                            </tbody>
                            </table>
                            </div>
                        </div>
                    </div>
                </section>
            </section>
        </>
    );
}
export default AddBook;
